mapper.insert:
INSERT INTO userprofile (
    id
    , pw
    , name
    , phone
    , address

) VALUES (
    #{id}
    , #{pw}
    , #{name}
    , #{phone}
    , #{address}
)

mapper.select:
SELECT *
FROM userprofile
WHERE  id = #{id}
AND pw = #{pw}
AND status != 'DELETE'

SELECT COUNT(id)
FROM userprofile
WHERE id = #{id}
